import { Component, Input } from '@angular/core';
import { ProcessoService } from '../processo.service';
import TextoCompleto from 'src/app/shared/model/texto-completo-model';

@Component({
  selector: 'app-quadro-texto',
  templateUrl: './quadro-texto.component.html',
  styleUrls: ['./quadro-texto.component.css']
})
export class QuadroTextoComponent {
  @Input()
  processo_id:String;

  textoCompleto: TextoCompleto;

  constructor(private service:ProcessoService){

  }
  ngOnInit(): void{
    this.fetchTextoCompleto(this.processo_id)
  }
  async fetchTextoCompleto(processo_id:String) {
      this.textoCompleto = await this.service.getTextoCompleto(processo_id);
      console.log(this.textoCompleto)
  }
}


<div class="container">

    <div class="col-md-12">
        <div class="card bg-light mb-3" style="margin-top: 3rem;">
            <div class="card-header">
                <h3> Texto Completo </h3>
            </div>
            <div class="card-body" style="padding-top: 0px;">
                <h4> Considerações </h4>
                <ul>
                    <li *ngFor="let consi of textoCompleto.considerandos">{{consi}}</li>
                </ul>
                <hr>
                <div *ngIf="textoCompleto.determinacoes.length != 1">
                    <h4> Determinações </h4>
                    <ul>
                        <li *ngFor="let det of textoCompleto.determinacoes">{{det}}</li>
                    </ul>
                    <hr>
                </div>
                <div *ngIf="textoCompleto.recomendacoes.length != 1">
                    <h4> Recomendações </h4>
                    <ul>
                        <li *ngFor="let rec of textoCompleto.recomendacoes">{{rec}}</li>
                    </ul>
                    <hr>
                </div>

            </div>
        </div>
    </div>
</div>
