import { Component, Input } from '@angular/core';
import { ProcessosApresentacaoService } from '../processos-apresentacao.service';
import ApresentaProcesso from 'src/app/shared/model/apresentacao-processo-model';

@Component({
  selector: 'app-apresentacao-processo',
  templateUrl: './apresentacao-processo.component.html',
  styleUrls: ['./apresentacao-processo.component.css']
})
export class ApresentacaoProcessoComponent {

  @Input()
  gestor:String;
  @Input()
  municipio:String;
  nome:String;

  apresentacoes: ApresentaProcesso[] = []


  constructor (private service: ProcessosApresentacaoService) {

  }

  ngOnInit() {
      this.fetchApresentacaoProcessos(this.gestor, this.municipio)
  }

  async fetchApresentacaoProcessos(gestor: String, municipio:String) {

    if(municipio === 'vazio' && gestor.length>0 ){
      this.apresentacoes = await this.service.getApresentacoesProcessosGestor(gestor);
      this.nome = gestor
    }
    else if (municipio.length > 0 && gestor === 'vazio'){
      this.apresentacoes = await this.service.getApresentacoesProcessosMunicipio(municipio);
      this.nome = municipio
    }
    console.log(this.apresentacoes);
  }
}

<div class="container">
    <!-- As "linhas" de coisas na tela são divididas em linhas -->
    <div class="row">
        <div class="col-md-12">
            <div class="card text-white bg-dark mb-3" style="margin: 20px 0px;">
                <div class="card-body">
                    <h1 class="no-margins">{{nome}}</h1>
                </div>
            </div>
        </div>
    </div>
    <!-- As "linhas" de coisas na tela são divididas em linhas -->
    <div class="row">
        <!-- dentro das linhas se coloca quadros com tamanho de colunas que devem somar tamanho 12 -->
        <div class="col-md-6" *ngFor="let processo of apresentacoes">
            <div class="card" style="margin: 10px 0px;">
                <div class="card-header">
                    <div class="row">
                        <div class="col-8">
                            <b><h5>{{processo.orgaoReferenteDecisao}}</h5></b>
                        </div>
                        <div class="col-4" style="justify-content: rig;">
                            <label class="pull-right font-bold text-success">Ano: {{processo.anoEmJulgamento}}</label>
                        </div>

                    </div>

                    <br>
                    <h5>{{processo.cidade}}</h5>
                </div>
                <div class="card-body" style="padding-top: 0px;">
                    <h2 class="no-margins">{{processo.resultadoDecisao}}</h2>
                    <a [href]="'/processo/'+processo.idDecisao" class="btn btn-block btn-outline btn-primary" style="margin-top: 10px;">Ver processo</a>
                </div>
                <div class="ibox-footer">
                    Data da decisão:
                    <strong>
                        <span class="text-success" th:text="${#temporals.format(processo.getDataJulgamento(),'dd/MM/yyyy')}">{{processo.dataDecisao}}</span>
                    </strong>
                </div>
            </div>
        </div>
    </div>

    <!-- fecha o container e o content -->
</div>
