import { Component } from '@angular/core';
import { EntradaService } from '../entrada.service';

@Component({
  selector: 'app-entrada-page',
  templateUrl: './entrada-page.component.html',
  styleUrls: ['./entrada-page.component.css']
})
export class EntradaPageComponent {

  numProcessos:Number;
  numMunicipios:Number;
  numResponsaveis:Number;


  constructor(private service:EntradaService){

  }

  ngOnInit(){
    this.fetchNumProcessos();
    this.fetchNumMunicipios();
    this.fetchNumResponsaveis();
  }
  async fetchNumResponsaveis() {
    this.numResponsaveis = await this.service.getNumResponsaveis();
  }
  async fetchNumMunicipios() {
    this.numMunicipios = await this.service.getNumMunicipios();
  }
  async fetchNumProcessos() {
    this.numProcessos = await this.service.getNumProcessos();
  }

}
<app-header></app-header>
<div class="container">
    <div class="row" style="margin-top: 50px;">
        <div class="col-md-4">
            <div class="card">
                <div class="card-header">
                    <h5>Processos</h5>
                </div>
                <div class="card-body" style="padding-top: 0px;">
                    <h1 class="no-margins" th:text="${numProcessos}"></h1>
                    <div class="font-bold text-info">Desde 2014</div>
                </div>
            </div>
        </div>
        <div class="col-md-4">
            <div class="card ">
                <div class="card-header">
                    <h5>Municípios</h5>
                </div>
                <div class="card-body" style="padding-top: 0px;">
                    <h1 class="no-margins" th:text="${numMunicipios}"></h1>
                    <div class="font-bold text-info">Municípios abrangidos</div>
                </div>
            </div>
        </div>
        <div class="col-md-4">
            <div class="card">
                <div class="card-header">
                    <h5>Responsáveis</h5>
                </div>
                <div class="card-body" style="padding-top: 0px;">
                    <h1 class="no-margins" th:text="${numPessoas}">{{nu}}</h1>
                    <div class="font-bold text-info"><b>Diferentes responsáveis</b></div>
                </div>
            </div>
        </div>
    </div>
</div>
