import { Component } from '@angular/core';
import Municipio from 'src/app/shared/model/municipio-model';
import PessoaGestora from 'src/app/shared/model/pessoa-model';
import { UtilsService } from '../utils.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})

export class HeaderComponent {
  municipios: Municipio[] = []
  pessoas: PessoaGestora[] = []

  constructor(private modService: UtilsService, private router: Router){}

  ngOnInit(): void{
    this.fetchPessoas();
    this.fetchMunicipios();

  }
  async fetchPessoas(): Promise<void> {
    this.pessoas = await this.modService.getPessoaGestoras();
    console.log(this.pessoas);

  }
  async fetchMunicipios(): Promise<void> {
    this.municipios = await this.modService.getMunicipios();
    console.log(this.municipios);

  }

  redirecionarGestor(event:any){
    const optionValue = event.target.value;
    this.router.navigate(['/processos-gestor', optionValue])
    console.log(window.location)
  }
  redirecionarMunicipio(event:any){
    const optionValue = event.target.value;
    this.router.navigate(['/processos-municipio', optionValue])
  }

}


<nav class="navbar navbar-expand-lg navbar-light bg-light">
    <a class="navbar-brand" href="#">Simplifica Decisões</a>
    <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarNav">
      <ul class="navbar-nav">
        <li class="nav-item">
        <select style="max-width: 250px; min-width: 250px; margin:0px 5px;" (change)="redirecionarGestor($event)">
            <option>Selecione Gestor</option>
            <option *ngFor="let pessoa of pessoas" [value] = "pessoa.name">
                {{pessoa.name}}
              </option>
        </select>
        </li>
        <li class="nav-item">
            <select style="max-width: 250px; min-width: 250px; margin:0px 5px;">
                <option>Selecione Município</option>
                <option *ngFor="let municipio of municipios" [value] = "municipio.nomeMunicipio" (change)="redirecionarMunicipio($event)">
                    {{municipio.nomeMunicipio}}
                  </option>
            </select>
        </li>
      </ul>
    </div>
  </nav>
