    <div class="col-md-12">
        <div class="card bg-light mb-3" style="margin-top: 3rem;">
            <div class="card-header">
                <h3> Texto Completo </h3>
            </div>
            <div class="card-body" style="padding-top: 0px;">
                <h4> Considerações </h4>
                <ul>
                    <li *ngFor="let consi of textoCompleto.considerandos">{{</li>
                </ul>
            </div>
        </div>
    </div>
</div>
